package taskFour;

public class Program7 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	        String msg = "Guvi Geek";

	        // Get the length of the string and storing in a variable 
	        int length = msg.length();

	        // Print the length
	        System.out.println("The length of the string \"" + msg + "\" is: " + length);
	        
	        // Print the length without saving length
	        System.out.println("The length of the string \"" + msg + "\" is: " + msg.length());
	}
}
